body.raw:
{
  "name": "My Application Key",
  "description": "API key for document management",
  "permissions": ["documents:read", "documents:write", "documents:send"]
}

{
  "message": "Please complete your signature for this important document. Your prompt attention is appreciated."
}

{
  "transactionId": "TXN-{{$randomUUID}}",
  "documentId": "{{testDocumentId}}",
  "status": "initiated",
  "participants": [
    {
      "name": "John Doe",
      "email": "john.doe@example.com",
      "role": "signer",
      "status": "pending"
    },
    {
      "name": "Jane Smith",
      "email": "jane.smith@example.com",
      "role": "signer",
      "status": "pending"
    }
  ],
  "reminderSettings": {
    "enabled": true,
    "frequency": "weekly",
    "maxReminders": 3
  },
  "deadlines": {
    "signatureDeadline": "2025-08-15T23:59:59.000Z"
  },
  "notes": "Important contract requiring signatures from both parties",
  "tags": ["contract", "urgent", "Q3-2025"]
}

{
  "status": "in_progress",
  "notes": "Updated: Document sent to all participants for signature",
  "participants": [
    {
      "name": "John Doe",
      "email": "john.doe@example.com",
      "role": "signer",
      "status": "signed",
      "signedAt": "2025-07-10T10:30:00.000Z"
    },
    {
      "name": "Jane Smith",
      "email": "jane.smith@example.com",
      "role": "signer",
      "status": "pending"
    }
  ],
  "adobeAgreementId": "CBJCHBCAABAAqnHRBWWP7YqePS4V6gTl",
  "tags": ["contract", "urgent", "Q3-2025", "in-progress"]
}

{
  "message": "Please complete your signature for this important document. Your prompt attention is appreciated.",
  "participantEmails": []
}

{
  "filter": {
    "status": "initiated",
    "createdAt": {
      "$gte": "2025-07-01T00:00:00.000Z",
      "$lte": "2025-07-31T23:59:59.999Z"
    }
  },
  "updates": {
    "status": "sent",
    "notes": "Bulk updated: Status changed from initiated to sent",
    "tags": ["bulk-updated", "july-2025"]
  }
}

{
  "filter": {
    "status": {
      "$in": ["sent", "in_progress"]
    },
    "isActive": true
  },
  "message": "Friendly reminder: Please complete your document signature at your earliest convenience.",
  "options": {
    "respectFrequency": true,
    "maxRemindersPerTransaction": 3
  }
}
